def is_possible(n, points):
    if n <= 2:
        return "YES"

    def collinear(p1, p2, p3):
        return (p2[1] - p1[1]) * (p3[0] - p1[0]) == (p3[1] - p1[1]) * (p2[0] - p1[0])

    def check_line(exclude):
        remaining = [points[i] for i in range(n) if i not in exclude]
        if len(remaining) <= 2:
            return True
        for i in range(2, len(remaining)):
            if not collinear(remaining[0], remaining[1], remaining[i]):
                return False
        return True

    for i in range(2, n):
        if not collinear(points[0], points[1], points[i]):
            if check_line({0, 1}) or check_line({0, i}) or check_line({1, i}):
                return "YES"
            else:
                return "NO"

    return "YES"

