MOD = 10**9 + 7

def solve(a, b, k, t):
    dp = [[0] * (2 * k * t + 1) for _ in range(2)]
    offset = k * t
    dp[0][a - b + offset] = 1

    for turn in range(1, t + 1):
        current = turn % 2
        previous = 1 - current
        for i in range(2 * k * t + 1):
            dp[current][i] = 0

        for i in range(2 * k * t + 1):
            if dp[previous][i] == 0:
                continue
            for j in range(-k, k + 1):
                for l in range(-k, k + 1):
                    ni = i + j - l
                    if 0 <= ni < 2 * k * t + 1:
                        dp[current][ni] = (dp[current][ni] + dp[previous][i]) % MOD

    result = 0
    for i in range(offset + 1, 2 * k * t + 1):
        result = (result + dp[t % 2][i]) % MOD

    print(result)

