def find_minimum_marks(n, l, x, y, marks):
    marks_set = set(marks)
    can_measure_x = False
    can_measure_y = False

    for mark in marks:
        if (mark + x) in marks_set or (mark - x) in marks_set:
            can_measure_x = True
        if (mark + y) in marks_set or (mark - y) in marks_set:
            can_measure_y = True

    if can_measure_x and can_measure_y:
        return 0, []

    if can_measure_x:
        for mark in marks:
            if (mark + y) <= l and (mark + y) not in marks_set:
                return 1, [mark + y]
            if (mark - y) >= 0 and (mark - y) not in marks_set:
                return 1, [mark - y]
        return 1, [y]

    if can_measure_y:
        for mark in marks:
            if (mark + x) <= l and (mark + x) not in marks_set:
                return 1, [mark + x]
            if (mark - x) >= 0 and (mark - x) not in marks_set:
                return 1, [mark - x]
        return 1, [x]

    for mark in marks:
        if (mark + x) <= l and (mark + x + y) <= l and (mark + x + y) in marks_set:
            return 1, [mark + x]
        if (mark - x) >= 0 and (mark - x + y) <= l and (mark - x + y) in marks_set:
            return 1, [mark - x]
        if (mark + y) <= l and (mark + y - x) >= 0 and (mark + y - x) in marks_set:
            return 1, [mark + y]
        if (mark - y) >= 0 and (mark - y + x) <= l and (mark - y + x) in marks_set:
            return 1, [mark - y]

    return 2, [x, y]

