def max_cake_volume(n, cakes):
    volumes = [(math.pi * r * r * h) for r, h in cakes]
    dp = [0] * n
    dp[0] = volumes[0]

    for i in range(1, n):
        max_vol = 0
        for j in range(i):
            if volumes[i] > volumes[j]:
                max_vol = max(max_vol, dp[j])
        dp[i] = max_vol + volumes[i]

    return max(dp)

