def can_decorate_tree(k1, k2, k3):
    from math import gcd
    from functools import reduce

    def lcm(a, b):
        return a * b // gcd(a, b)

    def lcm_multiple(*args):
        return reduce(lcm, args)

    lcm_all = lcm_multiple(k1, k2, k3)

    if lcm_all <= max(k1, k2, k3):
        return "YES"
    else:
        return "NO"

