def min_operations_to_delete_string(n, s):
    dp = [[0] * n for _ in range(n)]

    for length in range(1, n + 1):
        for i in range(n - length + 1):
            j = i + length - 1
            if length == 1:
                dp[i][j] = 1
            else:
                dp[i][j] = dp[i + 1][j] + 1
                if s[i] == s[i + 1]:
                    dp[i][j] = min(dp[i][j], dp[i + 1][j])
                for k in range(i + 1, j + 1):
                    if s[i] == s[k]:
                        dp[i][j] = min(dp[i][j], dp[i + 1][k - 1] + dp[k][j])

    return dp[0][n - 1]

