def determine_winner(n, ticket):
    first_half = ticket[:n//2]
    second_half = ticket[n//2:]

    sum_first_half = 0
    sum_second_half = 0
    question_first_half = 0
    question_second_half = 0

    for char in first_half:
        if char == '?':
            question_first_half += 1
        else:
            sum_first_half += int(char)

    for char in second_half:
        if char == '?':
            question_second_half += 1
        else:
            sum_second_half += int(char)

    diff = sum_first_half - sum_second_half
    question_diff = question_first_half - question_second_half

    if question_diff % 2 != 0:
        print("Monocarp")
        return

    if diff == (question_diff // 2) * 9:
        print("Bicarp")
    else:
        print("Monocarp")

