def count_ways(n, m):
    if m == 1:
        return (2 * n) % MOD

    dp = [[0] * (m + 1) for _ in range(n + 1)]
    dp[0][0] = 1

    for i in range(1, n + 1):
        for j in range(1, m + 1):
            dp[i][j] = (dp[i - 1][j] * 2 + dp[i - 1][j - 1] * 2) % MOD

    return dp[n][m]

