def find_min_max(m, s):
    if s == 0:
        if m == 1:
            return "0 0"
        else:
            return "-1 -1"

    if s > 9 * m:
        return "-1 -1"

    min_num = [0] * m
    max_num = [0] * m

    # Construct the minimum number
    sum_digits = s
    for i in range(m - 1, -1, -1):
        if sum_digits > 9:
            min_num[i] = 9
            sum_digits -= 9
        else:
            min_num[i] = sum_digits
            sum_digits = 0

    # Ensure no leading zero
    if min_num[0] == 0:
        for i in range(1, m):
            if min_num[i] > 0:
                min_num[i] -= 1
                min_num[0] = 1
                break

    # Construct the maximum number
    sum_digits = s
    for i in range(m):
        if sum_digits > 9:
            max_num[i] = 9
            sum_digits -= 9
        else:
            max_num[i] = sum_digits
            sum_digits = 0

    min_num_str = ''.join(map(str, min_num))
    max_num_str = ''.join(map(str, max_num))

    return f"{min_num_str} {max_num_str}"

