def count_dead_bulbs(s):
    n = len(s)
    color_positions = {'R': -1, 'B': -1, 'Y': -1, 'G': -1}
    dead_counts = {'R': 0, 'B': 0, 'Y': 0, 'G': 0}

    # Determine the positions of each color in the first 4 characters
    for i in range(4):
        if s[i] != '!':
            color_positions[s[i]] = i

    # Count dead bulbs
    for i in range(n):
        if s[i] == '!':
            pos = i % 4
            for color, position in color_positions.items():
                if position == pos:
                    dead_counts[color] += 1

