def find_swaps(s, t):
    if len(s) != len(t):
        return -1

    swaps = []
    visited = set()
    for i in range(len(s)):
        if s[i] != t[i] and (s[i], t[i]) not in visited and (t[i], s[i]) not in visited:
            swaps.append((s[i], t[i]))
            visited.add((s[i], t[i]))
            visited.add((t[i], s[i]))

    # Check if the swaps can actually fix the keyboard
    fixed = list(s)
    for a, b in swaps:
        for i in range(len(fixed)):
            if fixed[i] == a:
                fixed[i] = b
            elif fixed[i] == b:
                fixed[i] = a

    if ''.join(fixed) == t:
        print(len(swaps))
        for a, b in swaps:
            print(a, b)
    else:
        print(-1)

