def draw_brackets(n, sequence):
    def get_height(seq):
        max_depth = 0
        current_depth = 0
        for char in seq:
            if char == '[':
                current_depth += 1
                max_depth = max(max_depth, current_depth)
            else:
                current_depth -= 1
        return max_depth

    def draw(seq, depth, max_depth):
        if not seq:
            return [' ' * (2 * max_depth + 1)]

        if seq[0] == '[':
            close_index = 1
            balance = 1
            while balance != 0:
                if seq[close_index] == '[':
                    balance += 1
                else:
                    balance -= 1
                close_index += 1

            inner = draw(seq[1:close_index-1], depth + 1, max_depth)
            outer = draw(seq[close_index:], depth, max_depth)

            width = 2 * (max_depth - depth) + 1
            top_bottom = '+' + '-' * (width - 2) + '+'
            middle = '|' + ' ' * (width - 2) + '|'

            result = [top_bottom]
            for line in inner:
                result.append('|' + line + '|')
            result.append(middle)
            result.append(top_bottom)

            if outer:
                for i in range(len(result)):
                    result[i] += ' ' + outer[i]

            return result
        else:
            return draw(seq[1:], depth, max_depth)

    max_depth = get_height(sequence)
    result = draw(sequence, 0, max_depth)
    for line in result:
        print(line)

