def golden_compare(num1, num2):
    q = (5 ** 0.5 + 1) / 2

    def golden_value(num):
        value = 0
        power = len(num) - 1
        for digit in num:
            if digit == '1':
                value += q ** power
            power -= 1
        return value

    value1 = golden_value(num1)
    value2 = golden_value(num2)

    if value1 > value2:
        return ">"
    elif value1 < value2:
        return "<"
    else:
        return "="

