from itertools import permutations

def can_cross(bridge_parts, camels, order):
    for length, capacity in bridge_parts:
        current_weight = 0
        for i in range(len(order)):
            if current_weight + camels[order[i]] > capacity:
                return False
            current_weight += camels[order[i]]
            if i < len(order) - 1:
                current_weight -= camels[order[i]]
    return True

def min_distance(N, M, camels, bridge_parts):
    min_dist = float('inf')
    for order in permutations(range(N)):
        if can_cross(bridge_parts, camels, order):
            min_dist = min(min_dist, sum(bridge_parts[i][0] for i in range(M)))
    return min_dist if min_dist != float('inf') else -1

