def max_vk_occurrences(s):
    n = len(s)
    max_count = 0

    # Count initial VK occurrences
    initial_count = 0
    for i in range(n - 1):
        if s[i] == 'V' and s[i + 1] == 'K':
            initial_count += 1

    max_count = initial_count

    # Try changing each character and count VK occurrences
    for i in range(n):
        if s[i] == 'V':
            s_new = s[:i] + 'K' + s[i + 1:]
        elif s[i] == 'K':
            s_new = s[:i] + 'V' + s[i + 1:]
        else:
            continue

        new_count = 0
        for j in range(n - 1):
            if s_new[j] == 'V' and s_new[j + 1] == 'K':
                new_count += 1

        max_count = max(max_count, new_count)

    return max_count

