import math

def min_segments(n):
    side_length = math.isqrt(n)
    if side_length * side_length == n:
        return 2 * side_length
    elif side_length * (side_length + 1) >= n:
        return 2 * side_length + 1
    else:
        return 2 * side_length + 2

