def mod_exp(base, exp, mod):
    result = 1
    while exp > 0:
        if exp % 2 == 1:
            result = (result * base) % mod
        base = (base * base) % mod
        exp //= 2
    return result

def solve(a, b, p, x):
    count = 0
    inv_a = mod_exp(a, p-2, p)
    for n in range(1, p):
        lhs = (b * mod_exp(inv_a, n, p)) % p
        if lhs <= x:
            count += (x - lhs) // p + 1
    return count

