def min_seconds_to_happiness(n, x, y, c):
    def cells_covered_in_time(t):
        covered = 0
        for dx in range(-t, t + 1):
            for dy in range(-t, t + 1):
                if abs(dx) + abs(dy) <= t:
                    nx, ny = x + dx, y + dy
                    if 1 <= nx <= n and 1 <= ny <= n:
                        covered += 1
        return covered

    left, right = 0, 2 * n
    while left < right:
        mid = (left + right) // 2
        if cells_covered_in_time(mid) >= c:
            right = mid
        else:
            left = mid + 1

    return left

