    if first_class == -1:
        return 0

    # Count the number of pairs Alena stays at the university
    count = 0
    i = first_class
    while i <= last_class:
        if pairs[i] == 1:
            count += 1
            i += 1
        else:
            # Check for breaks of at least two free pairs in a row
            j = i
            while j <= last_class and pairs[j] == 0:
                j += 1
            if j - i >= 2:
                i = j
            else:
                count += (j - i)
                i = j

    return count

