def solve():
    input = sys.stdin.read
    data = input().split()

    N = int(data[0])
    M = int(data[1])
    P = int(data[2])

    edges = []
    index = 3
    for _ in range(M):
        A = int(data[index]) - 1
        B = int(data[index + 1]) - 1
        C = int(data[index + 2])
        edges.append((A, B, C))
        index += 3

    # Bellman-Ford to detect positive cycles reachable from vertex 1
    dist = [-float('inf')] * N
    dist[0] = 0

    for _ in range(N - 1):
        for u, v, c in edges:
            if dist[u] != -float('inf') and dist[v] < dist[u] + c - P:
                dist[v] = dist[u] + c - P

    # Check for positive cycles
    reachable_from_start = [False] * N
    reachable_from_end = [False] * N

    def dfs(v, graph, visited):
        stack = [v]
        while stack:
            node = stack.pop()
            if not visited[node]:
                visited[node] = True
                for neighbor in graph[node]:
                    if not visited[neighbor]:
                        stack.append(neighbor)

    graph = [[] for _ in range(N)]
    reverse_graph = [[] for _ in range(N)]

    for u, v, c in edges:
        graph[u].append(v)
        reverse_graph[v].append(u)

    dfs(0, graph, reachable_from_start)
    dfs(N - 1, reverse_graph, reachable_from_end)

    for u, v, c in edges:
        if dist[u] != -float('inf') and dist[v] < dist[u] + c - P:
            if reachable_from_start[u] and reachable_from_end[v]:
                print(-1)
                return

    print(max(0, dist[N - 1]))

