def min_coins(n, k, A, B):
    coins = 0
    while n > 1:
        if k == 1:
            coins += (n - 1) * A
            break
        if n % k == 0:
            cost_divide = B
            cost_subtract = (n - n // k) * A
            if cost_divide < cost_subtract:
                coins += cost_divide
                n //= k
            else:
                coins += cost_subtract
                n -= (n - n // k)
        else:
            remainder = n % k
            if remainder == 0:
                remainder = k
            if n - remainder > 0:
                coins += remainder * A
                n -= remainder
            else:
                coins += (n - 1) * A
                n = 1
    return coins

