def max_score(N, K, R, S, P, T):
    # Initialize the dp array to store the maximum score up to each round
    dp = [[0] * 3 for _ in range(N)]

    # Mapping for the hands
    hand_to_index = {'r': 0, 's': 1, 'p': 2}
    index_to_hand = ['r', 's', 'p']
    points = [P, R, S]  # Points for winning with Paper, Rock, Scissors respectively

    for i in range(N):
        for j in range(3):
            if i < K:
                if index_to_hand[j] == 'r' and T[i] == 's':
                    dp[i][j] = points[j]
                elif index_to_hand[j] == 's' and T[i] == 'p':
                    dp[i][j] = points[j]
                elif index_to_hand[j] == 'p' and T[i] == 'r':
                    dp[i][j] = points[j]
            else:
                max_prev = 0
                for l in range(3):
                    if l != j:
                        max_prev = max(max_prev, dp[i-K][l])
                if index_to_hand[j] == 'r' and T[i] == 's':
                    dp[i][j] = max_prev + points[j]
                elif index_to_hand[j] == 's' and T[i] == 'p':
                    dp[i][j] = max_prev + points[j]
                elif index_to_hand[j] == 'p' and T[i] == 'r':
                    dp[i][j] = max_prev + points[j]
                else:
                    dp[i][j] = max_prev

    # The answer is the maximum score we can get after all rounds
    print(max(dp[N-1]))

