def max_dominoes(n, m, edges):
    from itertools import product
    from collections import defaultdict

    def is_valid_assignment(assignment):
        vertex_dots = defaultdict(set)
        for (u, v), (a, b) in assignment.items():
            vertex_dots[u].add(a)
            vertex_dots[v].add(b)
            if len(vertex_dots[u]) > 1 or len(vertex_dots[v]) > 1:
                return False
        return True

    def backtrack(index, current_assignment):
        if index == len(edges):
            return len(current_assignment)

        u, v = edges[index]
        max_dominoes = 0
        for a, b in product(range(1, 7), repeat=2):
            if a <= b:
                current_assignment[(u, v)] = (a, b)
                if is_valid_assignment(current_assignment):
                    max_dominoes = max(max_dominoes, backtrack(index + 1, current_assignment))
                del current_assignment[(u, v)]

                current_assignment[(u, v)] = (b, a)
                if is_valid_assignment(current_assignment):
                    max_dominoes = max(max_dominoes, backtrack(index + 1, current_assignment))
                del current_assignment[(u, v)]

        max_dominoes = max(max_dominoes, backtrack(index + 1, current_assignment))
        return max_dominoes

    return backtrack(0, {})

