def can_form_rectangle(n, m, grid):
    # Find the bounding box of the 'X' characters
    min_row, max_row = n, 0
    min_col, max_col = m, 0

    for i in range(n):
        for j in range(m):
            if grid[i][j] == 'X':
                min_row = min(min_row, i)
                max_row = max(max_row, i)
                min_col = min(min_col, j)
                max_col = max(max_col, j)

    # Check if the bounding box forms a solid rectangle
    for i in range(min_row, max_row + 1):
        for j in range(min_col, max_col + 1):
            if grid[i][j] != 'X':
                return "NO"

    # Check if the bounding box can be doubled to form a rectangle
    height = max_row - min_row + 1
    width = max_col - min_col + 1

    if height * 2 <= n or width * 2 <= m:
        return "YES"
    else:
        return "NO"

