def process_bracket_sequence(n, m, p, brackets, operations):
    stack = []
    pair = [-1] * n
    for i in range(n):
        if brackets[i] == '(':
            stack.append(i)
        else:
            j = stack.pop()
            pair[i] = j
            pair[j] = i

    cursor = p - 1
    deleted = [False] * n

    for op in operations:
        if op == 'L':
            cursor -= 1
            while deleted[cursor]:
                cursor -= 1
        elif op == 'R':
            cursor += 1
            while deleted[cursor]:
                cursor += 1
        elif op == 'D':
            left = cursor
            right = pair[cursor]
            if left > right:
                left, right = right, left
            for i in range(left, right + 1):
                deleted[i] = True
            cursor = right + 1
            while cursor < n and deleted[cursor]:
                cursor += 1
            if cursor == n:
                cursor = left - 1
                while deleted[cursor]:
                    cursor -= 1

    result = []
    for i in range(n):
        if not deleted[i]:
            result.append(brackets[i])

    return ''.join(result)

