        count = 0
        while n % 2 == 0:
            n //= 2
            count += 1
        while n % 3 == 0:
            n //= 3
            count += 1
        while n % 5 == 0:
            n //= 5
            count += 1
        return n, count

    base_a, count_a = reduce_to_base(a)
    base_b, count_b = reduce_to_base(b)

    if base_a != base_b:
        return -1
    else:
        return abs(count_a - count_b)

