def min_cost_to_palindrome(n, a, b, c):
    cost = 0
    for i in range(n // 2):
        left = c[i]
        right = c[n - i - 1]

        if left == 2 and right == 2:
            cost += 2 * min(a, b)
        elif left == 2:
            cost += a if right == 0 else b
        elif right == 2:
            cost += a if left == 0 else b
        elif left != right:
            return -1

    return cost

