def min_clicks(n, m):
    from collections import deque

    queue = deque([(n, 0)])
    visited = set([n])

    while queue:
        current, clicks = queue.popleft()

        if current == m:
            return clicks

        if current * 2 not in visited and current < m:
            visited.add(current * 2)
            queue.append((current * 2, clicks + 1))

        if current - 1 not in visited and current > 1:
            visited.add(current - 1)
            queue.append((current - 1, clicks + 1))

