def find_min_v(cnt_1, cnt_2, x, y):
    def count_valid_numbers(v, prime):
        return v // prime

    def count_valid_numbers_both(v, prime1, prime2):
        return v // (prime1 * prime2)

    def is_valid(v):
        count_x = count_valid_numbers(v, x)
        count_y = count_valid_numbers(v, y)
        count_both = count_valid_numbers_both(v, x, y)
        valid_for_first = v - count_x
        valid_for_second = v - count_y
        valid_for_both = v - count_x - count_y + count_both
        return valid_for_first >= cnt_1 and valid_for_second >= cnt_2 and valid_for_both >= cnt_1 + cnt_2

    left, right = 1, 2 * (cnt_1 + cnt_2)
    while left < right:
        mid = (left + right) // 2
        if is_valid(mid):
            right = mid
        else:
            left = mid + 1
    return left

