def find_nice_word(s):
    n = len(s)
    if n < 26:
        return -1

    for i in range(n - 25):
        substring = s[i:i+26]
        if can_be_nice(substring):
            return fill_in_missing(s, i)

    return -1

def can_be_nice(substring):
    count = [0] * 26
    question_marks = 0

    for char in substring:
        if char == '?':
            question_marks += 1
        else:
            count[ord(char) - ord('A')] += 1

    needed = 26 - sum(1 for x in count if x > 0)
    return question_marks >= needed

def fill_in_missing(s, start):
    substring = list(s[start:start+26])
    count = [0] * 26
    for char in substring:
        if char != '?':
            count[ord(char) - ord('A')] += 1

    missing_chars = [chr(i + ord('A')) for i in range(26) if count[i] == 0]
    idx = 0

    for i in range(26):
        if substring[i] == '?':
            substring[i] = missing_chars[idx]
            idx += 1

    result = list(s)
    result[start:start+26] = substring

    for i in range(len(result)):
        if result[i] == '?':
            result[i] = 'A'

    return ''.join(result)

