def min_arithmetic_progressions(n, a):
    if n == 1:
        return 1

    def is_arithmetic_progression(start, end):
        if end - start <= 1:
            return True
        diff = None
        for i in range(start, end - 1):
            if a[i] == -1 or a[i + 1] == -1:
                continue
            if diff is None:
                diff = a[i + 1] - a[i]
            elif a[i + 1] - a[i] != diff:
                return False
        return True

    count = 0
    i = 0
    while i < n:
        j = i
        while j < n and (a[j] == -1 or (j + 1 < n and (a[j + 1] == -1 or is_arithmetic_progression(i, j + 2)))):
            j += 1
        count += 1
        i = j

    return count

