def divide_balloons(n, a):
    total_sum = sum(a)

    # If there's only one packet, it's impossible to divide
    if n == 1:
        return -1

    # Try to find a valid division
    for i in range(1, 1 << n):
        group1 = []
        group2 = []
        sum1 = 0
        sum2 = 0

        for j in range(n):
            if i & (1 << j):
                group1.append(j + 1)
                sum1 += a[j]
            else:
                group2.append(j + 1)
                sum2 += a[j]

        if sum1 != sum2 and len(group1) > 0 and len(group2) > 0:
            return (len(group1), group1)

    return -1

