def count_permutations_with_oddness(n, k):
    dp = [[0] * (k + 1) for _ in range(n + 1)]
    dp[0][0] = 1

    for i in range(1, n + 1):
        for j in range(k + 1):
            for l in range(i):
                if j >= abs(i - (l + 1)):
                    dp[i][j] = (dp[i][j] + dp[i - 1][j - abs(i - (l + 1))]) % MOD

    return dp[n][k]

