def prime_factors(x):
    factors = set()
    d = 2
    while d * d <= x:
        while (x % d) == 0:
            factors.add(d)
            x //= d
        d += 1
    if x > 1:
        factors.add(x)
    return factors

def g(y, p):
    result = 1
    while y % p == 0:
        y //= p
        result *= p
    return result

def f(x, y):
    primes = prime_factors(x)
    result = 1
    for p in primes:
        result *= g(y, p)
    return result

def calculate(x, n):
    primes = prime_factors(x)
    result = 1
    for p in primes:
        power = p
        while power <= n:
            result = (result * pow(p, n // power, MOD)) % MOD
            if power > n // p:
                break
            power *= p
    return result

