def digit_sum(x):
    return sum(int(digit) for digit in str(x))

def find_solutions(a, b, c):
    solutions = []
    for s in range(1, 82):  # s(x) can be at most 81 (for x = 999,999,999)
        x = b * (s ** a) + c
        if 0 < x < 10**9 and digit_sum(x) == s:
            solutions.append(x)
    return solutions

def main():
    import sys
    input = sys.stdin.read
    data = input().strip().split()
    a = int(data[0])
    b = int(data[1])
    c = int(data[2])

    solutions = find_solutions(a, b, c)
    solutions.sort()

    print(len(solutions))
    if solutions:
        print(" ".join(map(str, solutions)))

if __name__ == "__main__":
    main()

def will_get_tshirt(s, p):
    i = (s // 50) % 475
    for _ in range(25):
        i = (i * 96 + 42) % 475
        if 26 + i == p:
            return True
    return False

def min_successful_hacks(p, x, y):
    for k in range(0, 1000):
        for m in range(0, k + 1):
            score = x + 100 * m - 50 * (k - m)
            if score >= y and will_get_tshirt(score, p):
                return m
    return -1

