def is_single_plus(h, w, picture):
    def is_valid_center(x, y):
        if picture[x][y] != '*':
            return False
        # Check up
        if x == 0 or picture[x-1][y] != '*':
            return False
        # Check down
        if x == h-1 or picture[x+1][y] != '*':
            return False
        # Check left
        if y == 0 or picture[x][y-1] != '*':
            return False
        # Check right
        if y == w-1 or picture[x][y+1] != '*':
            return False
        return True

    def mark_visited(x, y):
        # Mark the center
        picture[x][y] = '.'
        # Mark up
        i = x - 1
        while i >= 0 and picture[i][y] == '*':
            picture[i][y] = '.'
            i -= 1
        # Mark down
        i = x + 1
        while i < h and picture[i][y] == '*':
            picture[i][y] = '.'
            i += 1
        # Mark left
        j = y - 1
        while j >= 0 and picture[x][j] == '*':
            picture[x][j] = '.'
            j -= 1
        # Mark right
        j = y + 1
        while j < w and picture[x][j] == '*':
            picture[x][j] = '.'
            j += 1

    center_found = False
    for i in range(h):
        for j in range(w):
            if is_valid_center(i, j):
                if center_found:
                    return "NO"
                center_found = True
                mark_visited(i, j)

    for i in range(h):
        for j in range(w):
            if picture[i][j] == '*':
                return "NO"

    return "YES" if center_found else "NO"

# Read input
h, w = map(int, input().split())
picture = [list(input().strip()) for _ in range(h)]

# Determine if the picture has a single "+" shape
result = is_single_plus(h, w, picture)
print(result)

def solve(n, a, b):
    if (a == 1 and b == 1 and n > 1) or (a > 1 and b > 1):
        print("NO")
        return

    print("YES")
    matrix = [[0] * n for _ in range(n)]

    if a == 1:
        for i in range(1, n):
            matrix[i][i-1] = matrix[i-1][i] = 1
    else:
        for i in range(n - a + 1):
            for j in range(i + 1, n - a + 1):
                matrix[i][j] = matrix[j][i] = 1

    for row in matrix:
        print("".join(map(str, row)))

# Example usage:
n, a, b = map(int, input().split())
solve(n, a, b)

def find_grid(s):
    if len(s) != 27:
        return "Impossible"

    # Create a dictionary to store the positions of each character in the string
    pos = {}
    for i, c in enumerate(s):
        if c in pos:
            pos[c].append(i)
        else:
            pos[c] = [i]

    # Check if there is any character that appears more than twice
    for key in pos:
        if len(pos[key]) > 2:
            return "Impossible"

    # Try to construct the grid
    grid = [['' for _ in range(13)] for _ in range(2)]
    used = set()
    for i in range(27):
        if s[i] in used:
            continue
        used.add(s[i])
        if len(pos[s[i]]) == 1:
            # Place the character in the first available position
            for r in range(2):
                for c in range(13):
                    if grid[r][c] == '':
                        grid[r][c] = s[i]
                        break
                if grid[r][c] == s[i]:
                    break
        else:
            # Place the character in two adjacent positions
            p1, p2 = pos[s[i]]
            r1, c1 = divmod(p1, 13)
            r2, c2 = divmod(p2, 13)
            if abs(r1 - r2) <= 1 and abs(c1 - c2) <= 1:
                grid[r1][c1] = s[i]
                grid[r2][c2] = s[i]
            else:
                return "Impossible"

    # Convert the grid to the required output format
    row1 = ''.join(grid[0])
    row2 = ''.join(grid[1])
    return row1 + '\n' + row2

# Read input
s = input().strip()

# Find and print the grid
result = find_grid(s)
print(result)

import math

def compare_powers(x, y):
    if x == y:
        return '='
    elif x == 1:
        return '<'
    elif y == 1:
        return '>'
    else:
        log_x = math.log(x)
        log_y = math.log(y)
        if y * log_x < x * log_y:
            return '<'
        elif y * log_x > x * log_y:
            return '>'
        else:
            return '='

x, y = map(int, input().split())
print(compare_powers(x, y))

def sum_of_digits(x):
    return sum(int(digit) for digit in str(x))

def find_smallest_root(n):
    for x in range(1, int(n**0.5) + 1):
        if x**2 + sum_of_digits(x) * x == n:
            return x
    return -1

