from collections import deque

def min_moves_to_corner(n, m, i, j, a, b):
    corners = [(1, 1), (1, m), (n, 1), (n, m)]
    directions = [(-a, -b), (a, -b), (-a, b), (a, b)]

    def is_within_bounds(x, y):
        return 1 <= x <= n and 1 <= y <= m

    def bfs(start):
        queue = deque([(start, 0)])
        visited = set()
        visited.add(start)

        while queue:
            (x, y), moves = queue.popleft()
            if (x, y) in corners:
                return moves
            for dx, dy in directions:
                nx, ny = x + dx, y + dy
                if is_within_bounds(nx, ny) and (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append(((nx, ny), moves + 1))
        return float('inf')

    if (i - 1) % a != 0 or (j - 1) % b != 0:
        return "Poor Inna and pony!"
    if (n - i) % a != 0 or (m - j) % b != 0:
        return "Poor Inna and pony!"

    min_moves = bfs((i, j))
    return min_moves if min_moves != float('inf') else "Poor Inna and pony!"

