def min_bills(n, k, denominations, q, requests):
    results = []
    for x in requests:
        min_bills_needed = float('inf')
        found = False
        for i in range(n):
            for j in range(i, n):
                d1, d2 = denominations[i], denominations[j]
                if d1 == d2:
                    if x % d1 == 0 and x // d1 <= k:
                        min_bills_needed = min(min_bills_needed, x // d1)
                        found = True
                else:
                    for b1 in range(k + 1):
                        if b1 * d1 > x:
                            break
                        if (x - b1 * d1) % d2 == 0:
                            b2 = (x - b1 * d1) // d2
                            if b1 + b2 <= k:
                                min_bills_needed = min(min_bills_needed, b1 + b2)
                                found = True
        if found:
            results.append(min_bills_needed)
        else:
            results.append(-1)
    return results

