def min_time_to_reach(s, x1, x2, t1, t2, p, d):
    if t2 <= t1:
        return abs(x2 - x1) * t2

    if d == 1:
        if p <= x1:
            tram_time = (x1 - p) * t1
        else:
            tram_time = (s - p + s + x1) * t1
    else:
        if p >= x1:
            tram_time = (p - x1) * t1
        else:
            tram_time = (p + s + s - x1) * t1

    tram_time += abs(x2 - x1) * t1
    walk_time = abs(x2 - x1) * t2

    return min(tram_time, walk_time)

