def find_inner_point(n, points):
    x_count = {}
    y_count = {}

    for x, y in points:
        if x in x_count:
            x_count[x] += 1
        else:
            x_count[x] = 1

        if y in y_count:
            y_count[y] += 1
        else:
            y_count[y] = 1

    for x, y in points:
        if x_count[x] > n and y_count[y] > n:
            return x, y

