from math import comb

def count_minimum_triples(n, a):
    a.sort()
    min_product = a[0] * a[1] * a[2]
    count = Counter(a[:3])

    if len(count) == 1:
        return comb(count[a[0]], 3)
    elif len(count) == 2:
        if count[a[0]] == 2:
            return comb(count[a[0]], 2) * count[a[1]]
        else:
            return count[a[0]] * comb(count[a[1]], 2)
    else:
        return count[a[0]] * count[a[1]] * count[a[2]]

