def count_standing_dominoes(n, s):
    standing_count = 0
    i = 0

    while i < n:
        if s[i] == '.':
            standing_count += 1
            i += 1
        elif s[i] == 'L':
            i += 1
        elif s[i] == 'R':
            j = i + 1
            while j < n and s[j] == '.':
                j += 1
            if j < n and s[j] == 'L':
                distance = j - i - 1
                if distance % 2 == 0:
                    standing_count += 1
            i = j

    return standing_count

