def find_permutation(n, m, leaders):
    a = [-1] * n
    used = [False] * (n + 1)

    for i in range(m - 1):
        current_leader = leaders[i] - 1
        next_leader = leaders[i + 1] - 1
        steps = (next_leader - current_leader) % n
        if steps == 0:
            steps = n

        if a[current_leader] == -1:
            if used[steps]:
                return -1
            a[current_leader] = steps
            used[steps] = True
        elif a[current_leader] != steps:
            return -1

    remaining = [i for i in range(1, n + 1) if not used[i]]
    for i in range(n):
        if a[i] == -1:
            a[i] = remaining.pop(0)

    return a

