def maximize_number(a, k):
    a = list(str(a))
    n = len(a)

    for i in range(n):
        if k == 0:
            break

        max_digit = a[i]
        max_pos = i

        for j in range(i + 1, min(n, i + k + 1)):
            if a[j] > max_digit:
                max_digit = a[j]
                max_pos = j

        if max_pos != i:
            for j in range(max_pos, i, -1):
                a[j], a[j - 1] = a[j - 1], a[j]
            k -= (max_pos - i)

    return ''.join(a)

# Read input
a, k = input().split()
k = int(k)

# Get the result
