# Print the result
print(result)

def max_distance(n, colors):
    # Find the first and last occurrence of each color
    first_occurrence = {}
    last_occurrence = {}

    for i in range(n):
        color = colors[i]
        if color not in first_occurrence:
            first_occurrence[color] = i
        last_occurrence[color] = i

    # Get the first and last house colors
    first_color = colors[0]
    last_color = colors[-1]

    # Calculate the maximum distance
    max_dist = 0
    for color in first_occurrence:
        if color != first_color:
            max_dist = max(max_dist, last_occurrence[color])
        if color != last_color:
            max_dist = max(max_dist, n - 1 - first_occurrence[color])

    return max_dist

