def possible_final_colors(n, s):
    from collections import Counter

    count = Counter(s)
    b, g, r = count['B'], count['G'], count['R']

    # If all cards are of the same color
    if (b == n) or (g == n) or (r == n):
        return ''.join(sorted(set(s)))

    # If there are cards of all three colors
    if b > 0 and g > 0 and r > 0:
        return 'BGR'

    # If there are cards of exactly two colors
    if b > 0 and g > 0:
        if b % 2 == 0 and g % 2 == 0:
            return 'G'
        elif b % 2 == 1 and g % 2 == 1:
            return 'BGR'
        else:
            return 'B'
    if b > 0 and r > 0:
        if b % 2 == 0 and r % 2 == 0:
            return 'R'
        elif b % 2 == 1 and r % 2 == 1:
            return 'BGR'
        else:
            return 'B'
    if g > 0 and r > 0:
        if g % 2 == 0 and r % 2 == 0:
            return 'R'
        elif g % 2 == 1 and r % 2 == 1:
            return 'BGR'
        else:
            return 'G'

# Read input
n = int(input().strip())
s = input().strip()

# Get the possible final colors
result = possible_final_colors(n, s)

# Print the result
print(result)
